/**
 * Interprets all border-width/style/color related properties in consistent order and returns normalized result
 *  with end-values for each side (left, right, top, bottom)
 *  that can be easily translated into styling properties of the client view library
 *
 * Priority is defined this way: (e.g. right border with) (last defined value wins)
 *  1) border: [1, solid, black]
 *  2) borderWidth: [1, 2, 3]
 *  3) borderRight: [3, solid, black]
 *  4) borderRightWidth: 4
 *
 * @param s - input styles
 * @return {{width, style, color}}
 */
export declare function normalizeBorder(s: any): {
    width: {
        left: any;
        right: any;
        top: any;
        bottom: any;
        hEqual: boolean;
        vEqual: boolean;
    };
    style: any;
    color: {
        left: any;
        right: any;
        top: any;
        bottom: any;
        hEqual: boolean;
        vEqual: boolean;
    };
};
