export type Optional<T, K extends keyof T> = Omit<T, K> & Partial<Pick<T, K>>;
export type Id = string;
export interface Board {
    id: Id;
    title: string;
    position: number;
    description?: string;
    isPublic?: boolean;
}
export interface Tag {
    id: Id;
    board: {
        id: Id;
    };
    title: string;
    color: string;
    position: number;
}
export interface Member {
    board: {
        id: Id;
    };
    user: string;
    fullName?: string;
    photo?: string | null;
}
export interface ItemFile {
    hash: string;
    name?: string;
    size?: number;
    type?: string;
}
